mapper.update:
UPDATE tb_user
SET password = HEX(AES_ENCRYPT(#{password}, username))
WHERE id = #{userId}

mapper.select:
SELECT DISTINCT u.id, u.name, u.sex, u.tel, u.email, d.dept_name AS dept, u.hiredate, u.root, u.status, (SELECT GROUP_CONCAT(role_name SEPARATOR ',') FROM tb_role WHERE JSON_CONTAINS(u.role, CONVERT(id, char) )) AS roles FROM tb_user u JOIN tb_role r ON JSON_CONTAINS(u.role, CONVERT(r.id, char)) LEFT JOIN tb_dept d ON u.dept_id = d.id WHERE 1 = 1
AND u.name LIKE '%${name}%'
AND u.sex = #{sex}
AND r.role_name = #{role}
AND d.id = #{deptId}
AND u.status = #{status}
LIMIT #{start}, #{length}

SELECT COUNT(DISTINCT u.id)
FROM tb_user u
JOIN tb_role r ON JSON_CONTAINS(u.role, CONVERT(r.id, char))
WHERE 1 = 1

    AND u.name LIKE "%${name}%"


    AND u.sex = #{sex}


    AND r.role_name = #{role}


    AND u.dept_id = #{deptId}


    AND u.status = #{status}
